import React, { useState, useEffect } from 'react';
import {
  Container,
  Box,
  Card,
  CardContent,
  CardMedia,
  Typography,
  TextField,
  Button,
  Stack,
  IconButton
} from '@mui/material';
import { Favorite, Comment, Share } from '@mui/icons-material';

const Home = () => {
  const [posts, setPosts] = useState([]);
  const [newPost, setNewPost] = useState('');

  const handlePostSubmit = async (e) => {
    e.preventDefault();
    // TODO: Implement post creation
    console.log('New post:', newPost);
    setNewPost('');
  };

  return (
    <Container>
      <Box sx={{ my: 4 }}>
        {/* Post Creation */}
        <Card sx={{ mb: 4 }}>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              Create Post
            </Typography>
            <form onSubmit={handlePostSubmit}>
              <TextField
                fullWidth
                multiline
                rows={3}
                placeholder="What's on your mind?"
                value={newPost}
                onChange={(e) => setNewPost(e.target.value)}
                sx={{ mb: 2 }}
              />
              <Button 
                variant="contained" 
                type="submit"
                disabled={!newPost.trim()}
              >
                Post
              </Button>
            </form>
          </CardContent>
        </Card>

        {/* Posts Feed */}
        <Stack spacing={2}>
          {posts.map((post) => (
            <Card key={post._id}>
              <CardContent>
                <Typography variant="h6">
                  {post.author.username}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {new Date(post.createdAt).toLocaleString()}
                </Typography>
                <Typography variant="body1" sx={{ mt: 2 }}>
                  {post.content}
                </Typography>
                {post.mediaUrl && (
                  <CardMedia
                    component="img"
                    height="194"
                    image={post.mediaUrl}
                    alt="Post media"
                  />
                )}
                <Box sx={{ mt: 2, display: 'flex', gap: 1 }}>
                  <IconButton>
                    <Favorite />
                  </IconButton>
                  <IconButton>
                    <Comment />
                  </IconButton>
                  <IconButton>
                    <Share />
                  </IconButton>
                </Box>
              </CardContent>
            </Card>
          ))}
        </Stack>
      </Box>
    </Container>
  );
};

export default Home;
